import './App.css';
import { DragDropContainer, DropTarget } from 'react-drag-drop-container';
import React, { useState } from "react";
import pizza from './pizza.png';
import broccoli from './broccoli.png';
import mouth from './mouth.gif';
import jumpscare from './jumpscare.gif'
import trans from './transparent.png'

function App() {

  let [amountOfPizza, setPizza] = useState(0);
  let [playVideo, setVideo] = useState("");
  let [zIndex, setZIndex] = useState(-1);
  let [color, setColor] = useState("black");
  let pizzas = <DragDropContainer
  targetKey="mouth"
  dropData={{ type: "pizza" }}
  dragData={{type: "pizza"}}
  id="pizza"
>
  <img
    src={pizza}
    width="100px"
    alt=""
  />
</DragDropContainer>; 
let broccolis = <DragDropContainer
targetKey="mouth"
dropData={{ type: "broccoli" }}
dragData={{type: "broccoli"}}
>
<img
  src={broccoli}
  width="100px"
  alt=""
/>
</DragDropContainer>;

  return (
    <>
      <h1> PIZZA OR DIE </h1>
      <p className="counter">Pizza Counter: <span style={{color: color}}>{amountOfPizza}</span></p>
      <div><img src={playVideo} style={{height:"100%", width: "100%", position: "fixed", marginTop: "-225px", backgroundSize: "cover", zIndex: zIndex}}/></div>
      <div className="game">
        <DragDropContainer
        targetKey="mouth"
        dropData={{ type: "pizza" }}
        dragData={{type: "pizza"}}
        id="pizza"
      >
        <img
          src={pizza}
          width="100px"
          alt=""
        />
      </DragDropContainer>

      <span style={{zIndex: 1, marginRight: "-40px"}}>{pizzas}</span>
      <span style={{zIndex: 2, marginRight: "-20px"}}>{pizzas}</span>
      <span style={{zIndex: 3, marginRight:"-150px"}}>{pizzas}</span>
      <span style={{zIndex: 4}}>{pizzas}</span>
      <span style={{zIndex: 5}}>{pizzas}</span>
      <span style={{zIndex: 6, marginRight: "-160px"}}>{pizzas}</span>

      <DragDropContainer
        targetKey="mouth"
        dropData={{ type: "broccoli" }}
        dragData={{type: "broccoli"}}
      >
        <img
          src={broccoli}
          width="100px"
          alt=""
        />
      </DragDropContainer>

      <span style={{zIndex: 6, marginRight: "-160px"}}>{broccolis}</span>
      <span style={{zIndex: 6, marginRight: "-160px"}}>{broccolis}</span>
      <span style={{zIndex: 6, marginRight: "-160px"}}>{broccolis}</span>
      <span style={{zIndex: 6}}>{broccolis}</span>

      <span style={{marginLeft: "650px"}}>
      <DropTarget
        id="my_target"
        targetKey="mouth"
        onHit={function (e) {
          if(e.dragData.type === "pizza") {
            setPizza((amountOfPizza) => amountOfPizza + 1);
            setColor(color = 'red');
          } else {
            setVideo(playVideo = jumpscare);
            setZIndex(zIndex = 7);
          }
          e.containerElem.style.visibility = 'hidden';
        }}
      >
        <img
          src={mouth}
          width="250px"
          alt=""
        />
      </DropTarget>
      </span>
        </div>
    </>
  );
}

export default App;
